import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import UserPosts from './UserPosts';
import './Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [avatarFile, setAvatarFile] = useState(null);
    const [avatarPreview, setAvatarPreview] = useState('');
    const [uploadingAvatar, setUploadingAvatar] = useState(false);
    const { token } = useContext(AuthContext);

    // Cloudinary configuration
    const CLOUDINARY_CLOUD_NAME = 'your_cloud_name'; // Replace with your cloud name
    const CLOUDINARY_UPLOAD_PRESET = 'your_upload_preset'; // Replace with your preset

    const fetchUserProfile = async () => {
        try {
            const response = await fetch('/api/users/profile', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            const data = await response.json();
            setUser(data);
        } catch (err) {
            setError('Failed to load profile');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (token) {
            fetchUserProfile();
        }
    }, [token]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    };

    const handleAvatarChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Validate file type
            if (!file.type.startsWith('image/')) {
                alert('Please select an image file');
                return;
            }
            
            // Validate file size (2MB max for avatars)
            if (file.size > 2 * 1024 * 1024) {
                alert('Avatar image size should be less than 2MB');
                return;
            }

            setAvatarFile(file);
            
            // Create preview URL
            const previewUrl = URL.createObjectURL(file);
            setAvatarPreview(previewUrl);
        }
    };

    const uploadAvatarToCloudinary = async (file) => {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('upload_preset', CLOUDINARY_UPLOAD_PRESET);
        formData.append('folder', 'social-media-avatars');
        // Add transformation for avatar optimization
        formData.append('transformation', 'w_200,h_200,c_fill,g_face,r_max');

        try {
            const response = await fetch(
                `https://api.cloudinary.com/v1_1/${CLOUDINARY_CLOUD_NAME}/image/upload`,
                {
                    method: 'POST',
                    body: formData
                }
            );

            if (!response.ok) {
                throw new Error('Failed to upload avatar');
            }

            const data = await response.json();
            return data.secure_url;
        } catch (error) {
            console.error('Cloudinary avatar upload error:', error);
            throw error;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setError('');
        
        try {
            let updatedUser = { ...user };
            
            // Upload avatar if file is selected
            if (avatarFile) {
                setUploadingAvatar(true);
                try {
                    const avatarUrl = await uploadAvatarToCloudinary(avatarFile);
                    updatedUser.profilePicture = avatarUrl;
                } catch (error) {
                    alert('Failed to upload avatar. Please try again.');
                    return;
                } finally {
                    setUploadingAvatar(false);
                }
            }

            const response = await fetch('/api/users/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(updatedUser),
            });
            
            if (response.ok) {
                setUser(updatedUser);
                setIsEditing(false);
                setAvatarFile(null);
                if (avatarPreview) {
                    URL.revokeObjectURL(avatarPreview);
                    setAvatarPreview('');
                }
            } else {
                setError('Failed to update profile');
            }
        } catch (err) {
            setError('Failed to update profile. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    const removeAvatar = () => {
        setAvatarFile(null);
        if (avatarPreview) {
            URL.revokeObjectURL(avatarPreview);
            setAvatarPreview('');
        }
    };

    if (loading) return <div className="profile-loading">Loading profile...</div>;
    if (error && !user) return <div className="profile-error">{error}</div>;
    if (!user) return <div className="profile-error">No user data found</div>;

    return (
        <div className="profile-page">
            <div className="profile-container">
                {isEditing ? (
                    <div className="profile-edit-section">
                        <div className="profile-edit-container">
                            <div className="edit-header">
                                <h1>Edit Profile</h1>
                                <p>Update your information to personalize your experience</p>
                            </div>

                            {error && (
                                <div className="error-message">{error}</div>
                            )}

                            <form onSubmit={handleSubmit} className="edit-form">
                                <div className="form-section">
                                    <h3>Basic Information</h3>
                                    <div className="form-row">
                                        <div className="form-group">
                                            <label>Full Name *</label>
                                            <input
                                                type="text"
                                                name="name"
                                                value={user.name || ''}
                                                onChange={handleChange}
                                                placeholder="Enter your full name"
                                                required
                                                disabled={isSubmitting}
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label>Email Address</label>
                                            <input
                                                type="email"
                                                value={user.email || ''}
                                                disabled
                                                className="disabled-input"
                                            />
                                            <small>Email address cannot be changed</small>
                                        </div>
                                    </div>
                                </div>

                                <div className="form-section">
                                    <h3>Profile Picture</h3>
                                    <div className="profile-pic-section">
                                        <div className="current-pic">
                                            <img 
                                                src={avatarPreview || user.profilePicture || '/default-profile.png'} 
                                                alt="Profile" 
                                            />
                                        </div>
                                        <div className="pic-input">
                                            <div className="upload-controls">
                                                <label htmlFor="avatar-upload" className="avatar-upload-label">
                                                    ðŸ“· Change Avatar
                                                </label>
                                                <input
                                                    id="avatar-upload"
                                                    type="file"
                                                    accept="image/*"
                                                    onChange={handleAvatarChange}
                                                    disabled={isSubmitting}
                                                    style={{ display: 'none' }}
                                                />
                                                
                                                {avatarFile && (
                                                    <button 
                                                        type="button" 
                                                        className="remove-avatar-btn"
                                                        onClick={removeAvatar}
                                                        disabled={isSubmitting}
                                                    >
                                                        Remove
                                                    </button>
                                                )}
                                            </div>
                                            
                                            {avatarFile && (
                                                <div className="file-info">
                                                    {avatarFile.name} ({(avatarFile.size / 1024 / 1024).toFixed(2)}MB)
                                                </div>
                                            )}
                                            
                                            <small>Upload a square image for best results. Max size: 2MB</small>
                                            
                                            {/* URL input as fallback */}
                                            <div className="url-fallback">
                                                <label>Or enter image URL</label>
                                                <input
                                                    type="url"
                                                    name="profilePicture"
                                                    value={user.profilePicture || ''}
                                                    onChange={handleChange}
                                                    placeholder="https://example.com/your-photo.jpg"
                                                    disabled={isSubmitting || !!avatarFile}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="form-section">
                                    <h3>About You</h3>
                                    <div className="form-group">
                                        <label>Bio</label>
                                        <textarea
                                            name="bio"
                                            value={user.bio || ''}
                                            onChange={handleChange}
                                            placeholder="Tell others about yourself..."
                                            rows="4"
                                            disabled={isSubmitting}
                                            maxLength="300"
                                        />
                                        <small>Share a bit about yourself ({(user.bio || '').length}/300)</small>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button 
                                        type="button" 
                                        className="btn-cancel" 
                                        onClick={() => {
                                            setIsEditing(false);
                                            removeAvatar();
                                        }}
                                        disabled={isSubmitting}
                                    >
                                        Cancel
                                    </button>
                                    <button 
                                        type="submit" 
                                        className="btn-save"
                                        disabled={isSubmitting || uploadingAvatar}
                                    >
                                        {uploadingAvatar ? 'Uploading Avatar...' : isSubmitting ? 'Saving...' : 'Save Changes'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                ) : (
                    <>
                        <div className="profile-info-section">
                            <div className="profile-display-section">
                                <div className="profile-header">
                                    <div className="profile-avatar">
                                        <img 
                                            src={user.profilePicture || '/default-profile.png'} 
                                            alt="Profile" 
                                            className="profile-image"
                                        />
                                    </div>
                                    <div className="profile-info">
                                        <h1 className="profile-name">{user.name || 'No name provided'}</h1>
                                        <p className="profile-email">{user.email}</p>
                                        <p className="profile-bio">{user.bio || 'No bio available'}</p>
                                    </div>
                                </div>
                                
                                <div className="profile-actions">
                                    <button 
                                        className="edit-profile-btn" 
                                        onClick={() => setIsEditing(true)}
                                    >
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="profile-posts-section">
                            <div className="posts-section-content">
                                <UserPosts />
                            </div>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Profile;
