import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import UserPosts from './UserPosts';
import './Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const { token } = useContext(AuthContext);

    const fetchUserProfile = async () => {
        try {
            const response = await fetch('/api/users/profile', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            const data = await response.json();
            setUser(data);
        } catch (err) {
            setError('Failed to load profile');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (token) {
            fetchUserProfile();
        }
    }, [token]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setError('');
        
        try {
            const response = await fetch('/api/users/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(user),
            });
            
            if (response.ok) {
                setIsEditing(false);
            } else {
                setError('Failed to update profile');
            }
        } catch (err) {
            setError('Failed to update profile. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    if (loading) return <div className="profile-loading">Loading profile...</div>;
    if (error && !user) return <div className="profile-error">{error}</div>;
    if (!user) return <div className="profile-error">No user data found</div>;

    return (
        <div className="profile-page">
            <div className="profile-container">
                {isEditing ? (
                    <div className="profile-edit-section">
                        <div className="profile-edit-container">
                            <div className="edit-header">
                                <h1>Edit Profile</h1>
                                <p>Update your information to personalize experience</p>
                            </div>

                            {error && (
                                <div className="error-message">{error}</div>
                            )}

                            <form onSubmit={handleSubmit} className="edit-form">
                                <div className="form-section">
                                    <h3>Basic Information</h3>
                                    <div className="form-row">
                                        <div className="form-group">
                                            <label>Full Name *</label>
                                            <input
                                                type="text"
                                                name="name"
                                                value={user.name || ''}
                                                onChange={handleChange}
                                                placeholder="Enter your full name"
                                                required
                                                disabled={isSubmitting}
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label>Email Address</label>
                                            <input
                                                type="email"
                                                value={user.email || ''}
                                                disabled
                                                className="disabled-input"
                                            />
                                            <small>Email address cannot be changed</small>
                                        </div>
                                    </div>
                                </div>

                                <div className="form-section">
                                    <h3>Profile Picture</h3>
                                    <div className="profile-pic-section">
                                        <div className="current-pic">
                                            <img 
                                                src={user.profilePicture || '/default-profile.png'} 
                                                alt="Profile" 
                                            />
                                        </div>
                                        <div className="pic-input">
                                            <label>Picture URL</label>
                                            <input
                                                type="url"
                                                name="profilePicture"
                                                value={user.profilePicture || ''}
                                                onChange={handleChange}
                                                placeholder="https://example.com/your-photo.jpg"
                                                disabled={isSubmitting}
                                            />
                                            <small>Enter a URL to an image that represents you</small>
                                        </div>
                                    </div>
                                </div>

                                <div className="form-section">
                                    <h3>About You</h3>
                                    <div className="form-group">
                                        {/* <label>Bio</label> */}
                                        <textarea
                                            name="bio"
                                            value={user.bio || ''}
                                            onChange={handleChange}
                                            placeholder="Tell others about yourself..."
                                            rows="4"
                                            disabled={isSubmitting}
                                            maxLength="300"
                                        />
                                        <small>Share a bit about yourself - your interests, profession, or anything you'd like others to know</small>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button 
                                        type="button" 
                                        className="btn-cancel" 
                                        onClick={() => setIsEditing(false)}
                                        disabled={isSubmitting}
                                    >
                                        Cancel
                                    </button>
                                    <button 
                                        type="submit" 
                                        className="btn-save"
                                        disabled={isSubmitting}
                                    >
                                        {isSubmitting ? 'Saving...' : 'Save Changes'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                ) : (
                    <>
                        <div className="profile-info-section">
                            <div className="profile-display-section">
                                <div className="profile-header">
                                    <div className="profile-avatar">
                                        <img 
                                            src={user.profilePicture || '/default-profile.png'} 
                                            alt="Profile" 
                                            className="profile-image"
                                        />
                                    </div>
                                    <div className="profile-info">
                                        <h1 className="profile-name">{user.name || 'No name provided'}</h1>
                                        <p className="profile-email">{user.email}</p>
                                        <p className="profile-bio">{user.bio || 'No bio available'}</p>
                                    </div>
                                </div>
                                
                                <div className="profile-actions">
                                    <button 
                                        className="edit-profile-btn" 
                                        onClick={() => setIsEditing(true)}
                                    >
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        </div>

                        <div className="profile-posts-section">
                            <div className="posts-section-content">
                                <UserPosts />
                            </div>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Profile;
