import React, { useState } from 'react';
import { FiHeart } from 'react-icons/fi';
import './Post.css';

// Utility function to format timestamps
const formatTimestamp = (timestamp) => {
    const now = new Date();
    const postDate = new Date(timestamp);
    const diffInSeconds = Math.floor((now - postDate) / 1000);

    if (diffInSeconds < 60) {
        return 'Just now';
    } else if (diffInSeconds < 3600) {
        const minutes = Math.floor(diffInSeconds / 60);
        return `${minutes}m ago`;
    } else if (diffInSeconds < 86400) {
        const hours = Math.floor(diffInSeconds / 3600);
        return `${hours}h ago`;
    } else if (diffInSeconds < 604800) {
        const days = Math.floor(diffInSeconds / 86400);
        return `${days}d ago`;
    } else {
        return postDate.toLocaleDateString('en-US', {
            month: 'short',
            day: 'numeric',
            year: postDate.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
        });
    }
};

const Post = ({ post }) => {
    const [likes, setLikes] = useState(post.likes);
    const [isLiked, setIsLiked] = useState(false);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
    const interval = setInterval(() => {
        // Force re-render every minute to update relative time
        setLikes(prev => prev); // This triggers a re-render
    }, 60000); // Update every minute

    return () => clearInterval(interval);
    }, []);

    const handleLike = async () => {
        if (isLoading) return; // Prevent double clicks

        setIsLoading(true);

        try {
            const response = await fetch(`/api/posts/${post._id}/like`, {
                method: 'POST',
            });
            const data = await response.json();

            if (response.ok) {
                // Only update with server response, no optimistic updates
                setLikes(data.likes);
                // You could track liked state differently if needed
            }
        } catch (error) {
            console.error('Error liking post:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="post-card">
            <div className="post-header">
                <img 
                    src={post.author.profile_picture || '/default-profile.png'} 
                    alt={post.author.name} 
                    className="post-author-img"
                />
                <div className="post-author-info">
                    <span className="post-author-name">{post.author.name}</span>
                    <span className="post-timestamp">{formatTimestamp(post.created_at)}</span>
                </div>
            </div>
            <p className="post-content">{post.content}</p>
            {post.image && <img src={post.image} alt="" className="post-image" />}
            <div className="post-actions">
                <button 
                    onClick={handleLike} 
                    className={`like-button ${isLiked ? 'liked' : ''}`}
                    disabled={isLoading}
                >
                    <FiHeart />
                    <span>{likes}</span>
                </button>
            </div>
        </div>
    );
};

export default Post;
