import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import UserPosts from './UserPosts';
import './Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [showSuccess, setShowSuccess] = useState(false);
    const [fieldErrors, setFieldErrors] = useState({});
    const { token } = useContext(AuthContext);

    const fetchUserProfile = async () => {
        try {
            const response = await fetch('/api/users/profile', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            const data = await response.json();
            setUser(data);
        } catch (err) {
            setError('Failed to load profile');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (token) {
            fetchUserProfile();
        }
    }, [token]);

    const validateField = (name, value) => {
        const errors = { ...fieldErrors };
        
        switch (name) {
            case 'name':
                if (!value.trim()) {
                    errors.name = 'Name is required';
                } else if (value.trim().length < 2) {
                    errors.name = 'Name must be at least 2 characters';
                } else {
                    delete errors.name;
                }
                break;
            case 'profilePicture':
                if (value && !isValidUrl(value)) {
                    errors.profilePicture = 'Please enter a valid URL';
                } else {
                    delete errors.profilePicture;
                }
                break;
            case 'bio':
                if (value.length > 300) {
                    errors.bio = 'Bio must be less than 300 characters';
                } else {
                    delete errors.bio;
                }
                break;
            default:
                break;
        }
        
        setFieldErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const isValidUrl = (string) => {
        try {
            new URL(string);
            return true;
        } catch (_) {
            return false;
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
        validateField(name, value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Validate all fields
        const isValid = ['name', 'profilePicture', 'bio'].every(field => 
            validateField(field, user[field] || '')
        );
        
        if (!isValid) return;
        
        setIsSubmitting(true);
        setError('');
        
        try {
            const response = await fetch('/api/users/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(user),
            });
            
            if (response.ok) {
                setShowSuccess(true);
                setTimeout(() => {
                    setShowSuccess(false);
                    setIsEditing(false);
                }, 2000);
            } else {
                throw new Error('Failed to update profile');
            }
        } catch (err) {
            setError('Failed to update profile. Please try again.');
        } finally {
            setIsSubmitting(false);
        }
    };

    const getBioCharacterCount = () => {
        const count = (user?.bio || '').length;
        const maxCount = 300;
        return { count, maxCount, isWarning: count > 250, isError: count > maxCount };
    };

    if (loading) return <div className="profile-loading">Loading profile...</div>;
    if (error && !user) return <div className="profile-error">{error}</div>;
    if (!user) return <div className="profile-error">No user data found</div>;

    return (
        <div className="profile-page">
            <div className="profile-container">
                {isEditing ? (
                    <div className="profile-edit-section">
                        <div className="profile-edit-container">
                            <div className="edit-profile-header">
                                <h1 className="edit-profile-title">Edit Your Profile</h1>
                                <p className="edit-profile-subtitle">
                                    Update your information to personalize your experience
                                </p>
                            </div>

                            {showSuccess && (
                                <div className="success-message">
                                    <span>âœ“</span>
                                    Profile updated successfully!
                                </div>
                            )}

                            {error && (
                                <div className="form-error" style={{ marginBottom: '2rem', padding: '1rem', background: 'rgba(244, 33, 46, 0.1)', borderRadius: '12px' }}>
                                    {error}
                                </div>
                            )}

                            <form onSubmit={handleSubmit} className="profile-edit-form">
                                {isSubmitting && (
                                    <div className="loading-overlay">
                                        <div className="loading-spinner"></div>
                                    </div>
                                )}

                                {/* Basic Information Section */}
                                <div className="form-section">
                                    <h3 className="form-section-title">Basic Information</h3>
                                    <div className="form-grid">
                                        <div className="form-field">
                                            <label className="form-label">
                                                Full Name <span className="required">*</span>
                                            </label>
                                            <input
                                                type="text"
                                                name="name"
                                                value={user.name || ''}
                                                onChange={handleChange}
                                                className="form-input"
                                                placeholder="Enter your full name"
                                                required
                                                disabled={isSubmitting}
                                            />
                                            {fieldErrors.name && (
                                                <div className="form-error">{fieldErrors.name}</div>
                                            )}
                                        </div>

                                        <div className="form-field">
                                            <label className="form-label">Email Address</label>
                                            <input
                                                type="email"
                                                value={user.email || ''}
                                                className="form-input"
                                                disabled
                                                style={{ opacity: 0.6, cursor: 'not-allowed' }}
                                            />
                                            <div className="form-help-text">
                                                Email address cannot be changed
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Profile Picture Section */}
                                <div className="form-section">
                                    <h3 className="form-section-title">Profile Picture</h3>
                                    <div className="profile-picture-section">
                                        <div className="current-picture">
                                            <img 
                                                src={user.profilePicture || '/default-profile.png'} 
                                                alt="Current profile" 
                                            />
                                        </div>
                                        <div className="picture-input-group">
                                            <div className="form-field">
                                                <label className="form-label">Picture URL</label>
                                                <input
                                                    type="url"
                                                    name="profilePicture"
                                                    value={user.profilePicture || ''}
                                                    onChange={handleChange}
                                                    className="form-input"
                                                    placeholder="https://example.com/your-photo.jpg"
                                                    disabled={isSubmitting}
                                                />
                                                {fieldErrors.profilePicture && (
                                                    <div className="form-error">{fieldErrors.profilePicture}</div>
                                                )}
                                                <div className="form-help-text">
                                                    Enter a URL to an image that represents you
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Bio Section */}
                                <div className="form-section">
                                    <h3 className="form-section-title">About You</h3>
                                    <div className="form-grid single">
                                        <div className="form-field">
                                            <label className="form-label">Bio</label>
                                            <textarea
                                                name="bio"
                                                value={user.bio || ''}
                                                onChange={handleChange}
                                                className="form-textarea"
                                                placeholder="Tell others about yourself, your interests, or what you do..."
                                                rows="4"
                                                disabled={isSubmitting}
                                                maxLength="300"
                                            />
                                            <div className={`character-count ${getBioCharacterCount().isWarning ? 'warning' : ''} ${getBioCharacterCount().isError ? 'error' : ''}`}>
                                                {getBioCharacterCount().count}/{getBioCharacterCount().maxCount}
                                            </div>
                                            {fieldErrors.bio && (
                                                <div className="form-error">{fieldErrors.bio}</div>
                                            )}
                                            <div className="form-help-text">
                                                Share a bit about yourself - your interests, profession, or anything you'd like others to know
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                <div className="form-actions">
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary" 
                                        onClick={() => setIsEditing(false)}
                                        disabled={isSubmitting}
                                    >
                                        Cancel
                                    </button>
                                    <button 
                                        type="submit" 
                                        className="btn btn-primary"
                                        disabled={isSubmitting || Object.keys(fieldErrors).length > 0}
                                    >
                                        {isSubmitting ? 'Saving...' : 'Save Changes'}
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                ) : (
                    <>
                        {/* User Information Section */}
                        <div className="profile-info-section">
                            <div className="profile-display-section">
                                <div className="profile-header">
                                    <div className="profile-avatar">
                                        <img 
                                            src={user.profilePicture || '/default-profile.png'} 
                                            alt="Profile" 
                                            className="profile-image"
                                        />
                                    </div>
                                    <div className="profile-info">
                                        <h1 className="profile-name">{user.name || 'No name provided'}</h1>
                                        <p className="profile-email">{user.email}</p>
                                        <p className="profile-bio">{user.bio || 'No bio available'}</p>
                                    </div>
                                </div>
                                
                                <div className="profile-actions">
                                    <button 
                                        className="edit-profile-btn" 
                                        onClick={() => setIsEditing(true)}
                                    >
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        </div>

                        {/* Posts Section */}
                        <div className="profile-posts-section">
                            <div className="posts-section-content">
                                <UserPosts />
                            </div>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Profile;
