import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import UserPosts from './UserPosts';
import './Profile.css';

const Profile = () => {
    const [user, setUser] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const { token } = useContext(AuthContext);

    const fetchUserProfile = async () => {
        try {
            const response = await fetch('/api/users/profile', {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            const data = await response.json();
            setUser(data);
        } catch (err) {
            setError('Failed to load profile');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        if (token) {
            fetchUserProfile();
        }
    }, [token]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await fetch('/api/users/profile', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(user),
            });
            setIsEditing(false);
        } catch (err) {
            setError('Failed to update profile');
        }
    };

    if (loading) return <div className="profile-loading">Loading profile...</div>;
    if (error) return <div className="profile-error">{error}</div>;
    if (!user) return <div className="profile-error">No user data found</div>;

    return (
        <div className="profile-page">
            <div className="profile-container">
                {isEditing ? (
                    <div className="profile-edit-section">
                        <div className="profile-edit-content">
                            <h2>Edit Your Profile</h2>
                            <form onSubmit={handleSubmit} className="profile-form">
                                <div className="form-group">
                                    <label htmlFor="name">Full Name</label>
                                    <input
                                        id="name"
                                        type="text"
                                        name="name"
                                        value={user.name || ''}
                                        onChange={handleChange}
                                        placeholder="Enter your full name"
                                        required
                                    />
                                </div>
                                
                                <div className="form-group">
                                    <label htmlFor="profilePicture">Profile Picture URL</label>
                                    <input
                                        id="profilePicture"
                                        type="url"
                                        name="profilePicture"
                                        value={user.profilePicture || ''}
                                        onChange={handleChange}
                                        placeholder="https://example.com/your-photo.jpg"
                                    />
                                </div>
                                
                                <div className="form-group">
                                    <label htmlFor="bio">Bio</label>
                                    <textarea
                                        id="bio"
                                        name="bio"
                                        value={user.bio || ''}
                                        onChange={handleChange}
                                        placeholder="Tell us about yourself..."
                                        rows="4"
                                    />
                                </div>
                                
                                <div className="form-actions">
                                    <button type="submit" className="save-btn">Save Changes</button>
                                    <button 
                                        type="button" 
                                        className="cancel-btn" 
                                        onClick={() => setIsEditing(false)}
                                    >
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                ) : (
                    <>
                        {/* User Information Section - Seamless infinite background */}
                        <div className="profile-info-section">
                            <div className="profile-display-section">
                                <div className="profile-header">
                                    <div className="profile-avatar">
                                        <img 
                                            src={user.profilePicture || '/default-profile.png'} 
                                            alt="Profile" 
                                            className="profile-image"
                                        />
                                    </div>
                                    <div className="profile-info">
                                        <h1 className="profile-name">{user.name || 'No name provided'}</h1>
                                        <p className="profile-email">{user.email}</p>
                                        <p className="profile-bio">{user.bio || 'No bio available'}</p>
                                    </div>
                                </div>
                                
                                <div className="profile-actions">
                                    <button 
                                        className="edit-profile-btn" 
                                        onClick={() => setIsEditing(true)}
                                    >
                                        Edit Profile
                                    </button>
                                </div>
                            </div>
                        </div>

                        {/* Posts Section - Seamless transition */}
                        <div className="profile-posts-section">
                            <div className="posts-section-content">
                                <UserPosts />
                            </div>
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default Profile;
