import React, { useState, useContext } from 'react';
import { useHistory, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { FcGoogle } from 'react-icons/fc'; // Import Google icon
import './Auth.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { login } = useContext(AuthContext);
    const history = useHistory();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, password })
            });

            if (!response.ok) {
                throw new Error('Login failed');
            }

            const data = await response.json();
            login(data.access_token);
            history.push('/');

        } catch (err) {
            setError('Invalid email or password');
        }
    };

    return (
        <div className="auth-page-container">
            <div className="auth-showcase">
                <div className="showcase-content">
                    <img src="/logo.png" alt="ChaiCircle Logo" className="auth-logo" />
                    <h1 className="brand-logo">ChaiCircle</h1>
                    <p className="tagline">Connect, Share, and Sip on Conversations.</p>
                </div>
            </div>
            <div className="auth-form-container">
                <div className="auth-container">
                    <h2>Welcome Back!</h2>
                    <p className="auth-subheading">Please enter your details to log in.</p>

                    <form onSubmit={handleLogin} className="auth-form">
                        <div>
                            <label>Email:</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div>
                            <label>Password:</label>
                            <input
                                type="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <button type="submit">Login</button>
                    </form>

                    {/* <div className="auth-divider">
                        <span>OR</span>
                    </div>

                    <button className="social-login-button">
                        <FcGoogle /> Continue with Google
                    </button> */}

                    <div className="switch-auth">
                        Don't have an account? <Link to="/signup">Sign Up</Link>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
